measures.expression:
SUMX (
    VALUES ( 'Product'[Model Code] ),
    VAR ModelCode = 'Product'[Model Code]

    VAR Opis = RIGHT(MAX('Product'[Model]), 6) = ", PLUS"

    // VAR WarunekPLUS =
    //     RIGHT ( ModelCode, 4 ) = "PLUS"

    VAR WarunekLista =
        ModelCode IN {
            "MTLBOARDMFCORGC",
            "MTLBOARDMFCORGD",
            "MTLBOARDFENIXRC",
            "MTLBOARDFENIXRD",
            "MTLBOARDMFCRC",
            "MTLBOARDMFCRD"
        }

    // VAR WarunekWykluczenie =
    //     ModelCode <> "TAKTIKOFFICEMESHPLUS"

    RETURN
        IF (
            ( Opis || WarunekLista ),
                //  WarunekWykluczenie,
            [Net Sales PLN],
            BLANK ()
        )
)
